function levenshtein(a, b) {
  const matrix = Array.from({ length: b.length + 1 }, (_, i) =>
    Array(a.length + 1).fill(0)
  );
  for (let i = 0; i <= a.length; i++) matrix[0][i] = i;
  for (let j = 0; j <= b.length; j++) matrix[j][0] = j;

  for (let j = 1; j <= b.length; j++) {
    for (let i = 1; i <= a.length; i++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      matrix[j][i] = Math.min(
        matrix[j][i - 1] + 1,
        matrix[j - 1][i] + 1,
        matrix[j - 1][i - 1] + cost
      );
    }
  }
  return matrix[b.length][a.length];
}

function fuzzyMatch(input, options) {
  input = input.toLowerCase().trim();
  let bestMatch = null;
  let bestScore = Infinity;
  for (const opt of options) {
    const score = levenshtein(input, opt.toLowerCase());
    if (score < bestScore) {
      bestScore = score;
      bestMatch = opt;
    }
  }
  return bestScore <= 6 ? bestMatch : null; // loosened threshold
}

const msg = ($json["message"] || "").toLowerCase();

const stations = {
  "kaladipet": ["kaladipet", "kaladpet"],
  "tollgate": ["tollgate", "tolgate"],
  "new washermenpet": ["new washermenpet"],
  "central": ["central", "chennai central"],
  "egmore": ["egmore"],
  "nehru park": ["nehru park", "nehru"],
  "kilpauk": ["kilpauk", "kilpauk medical college"],
  "pachaiyappa's": ["pachaiyappa's", "pachaiyappas"],
  "shenoy nagar": ["shenoy nagar"],
  "anna nagar east": ["anna nagar east"],
  "anna nagar tower": ["anna nagar tower"],
  "thirumangalam": ["thirumangalam"],
  "koyambedu": ["koyambedu", "koyembedu", "cmbt"],
  "vadapalani": ["vadapalani"],
  "arumbakkam": ["arumbakkam"],
  "ashok nagar": ["ashok nagar", "ashoknagar", "ashopnagar"],
  "st thomas mount": ["st thomas mount", "mount", "thomas mount"],
  "little mount": ["little mount"],
  "guindy": ["guindy"],
  "alandur": ["alandur", "alendur"],
  "nanganallur": ["nanganallur"],
  "meenambakkam": ["meenambakkam"],
  "airport": ["airport", "chennai airport"]
};

const formatStation = name =>
  name.split(' ').map(word => word[0].toUpperCase() + word.slice(1)).join(' ');

const aliasToStation = {};
for (const [station, aliases] of Object.entries(stations)) {
  for (const alias of aliases) aliasToStation[alias] = station;
}
const allAliases = Object.keys(aliasToStation);

let fromStation = "";
let toStation = "";

// Try to match "from X to Y", "dari X ke Y", or "X 2 Y"
let travelMatch =
  msg.match(/from\s+([a-z\s']+?)\s+to\s+([a-z\s']+)/i) ||
  msg.match(/dari\s+([a-z\s']+?)\s+ke\s+([a-z\s']+)/i) ||
  msg.match(/from\s+([a-z\s']+?)\s+2\s+([a-z\s']+)/i) ||
  msg.match(/dari\s+([a-z\s']+?)\s+2\s+([a-z\s']+)/i) ||
  msg.match(/([a-z\s']+?)\s+2\s+([a-z\s']+)/i);

if (travelMatch) {
  const fromGuess = fuzzyMatch(travelMatch[1].trim(), allAliases);
  const toGuess = fuzzyMatch(travelMatch[2].trim(), allAliases);
  fromStation = aliasToStation[fromGuess] || "";
  toStation = aliasToStation[toGuess] || "";
}

// fallback: fuzzy match each word in the message
if (!fromStation || !toStation) {
  const words = msg.split(/\s+/);
  const found = [];
  for (const word of words) {
    const match = fuzzyMatch(word, allAliases);
    if (match) found.push(aliasToStation[match]);
  }
  const unique = [...new Set(found)];
  if (unique.length >= 2) {
    fromStation = fromStation || unique[0];
    toStation = toStation || unique[1];
  }
}

// Generate Ticket ID
const generateTicketId = () => {
  const fromCode = fromStation.replace(/\s+/g, '').substring(0, 3).toUpperCase();
  const toCode = toStation.replace(/\s+/g, '').substring(0, 3).toUpperCase();
  const randomDigits = Math.floor(100000 + Math.random() * 900000);
  return `CMRL-${fromCode}-${toCode}-${randomDigits}`;
};

const calculateFare = (from, to) => {
  const all = Object.keys(stations);
  const fromIdx = all.indexOf(from);
  const toIdx = all.indexOf(to);
  if (fromIdx === -1 || toIdx === -1) return 0;
  const diff = Math.abs(fromIdx - toIdx);

  if (diff <= 3) return 16;
  if (diff <= 6) return 24;
  if (diff <= 9) return 32;
  return 40;
};

let reply = "";
let ticketId = "";
let fare = 0;
let qrCodeUrl = "";

if (fromStation && toStation) {
  if (fromStation === toStation) {
    reply = `âš ï¸ Your source and destination stations cannot be the same. Please try again.`;
  } else {
    ticketId = generateTicketId();
    fare = calculateFare(fromStation, toStation);
    const now = new Date();
    const dateStr = now.toLocaleDateString('en-IN');
    const timeStr = now.toLocaleTimeString('en-IN');

    const qrData = `CMRL TICKET\nID: ${ticketId}\nFROM: ${formatStation(fromStation)}\nTO: ${formatStation(toStation)}\nFARE: â‚¹${fare}\nDATE: ${dateStr}\nTIME: ${timeStr}`;

    qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=250x250&data=${encodeURIComponent(qrData)}`;

    reply = `ðŸŽ« *Ticket Confirmed*\n\n` +
            `*Ticket ID:* ${ticketId}\n` +
            `*From:* ${formatStation(fromStation)}\n` +
            `*To:* ${formatStation(toStation)}\n` +
            `*Fare:* â‚¹${fare}\n` +
            `*Date:* ${dateStr}\n` +
            `*Time:* ${timeStr}\n\n` +
            `Valid for one journey today. Show this message at stations.`;
  }
} else {
  const sample = Object.keys(stations).slice(0, 5).map(formatStation).join(', ');
  reply = `ðŸ‘‹ *Welcome to CMRL Ticket Booking!*\n\n` +
          `To book a ticket, reply like:\n` +
          `_Book from Ashok Nagar to Alandur_\n\n` +
          `Sample stations: ${sample}...`;
}

console.log("Transcription:", msg);
console.log("From guess:", fromStation);
console.log("To guess:", toStation);

return [{
  reply,
  from: $json.from || $json.From || $json.body?.From || 'unknown',
  ticketId,
  fromStation: fromStation ? formatStation(fromStation) : '',
  toStation: toStation ? formatStation(toStation) : '',
  fare,
  qrCodeUrl
}];
